//引入了axios，在此之前已经npm install 了axios,是一个javascript库
//目前来看axios就是用来连接api，用于发起Http请求和处理响应
import axios from 'axios';

//创建一个axios实例，使用axios
let service=axios.create({

    baseURL:'http://localhost:3000/',//本地服务器的地址
    timeout:3000
})



//导出实例

export default service
