import service from ".."  //上一级目录中获取某些东西

//导出一个具体的函数，里面返回了service实例，里面配置了对象
//封装axios
export function getBanner(){

    return service({

        method:"GET",
        url:"/banner?type=2",
    })

    
    
}

export function getMusicList(){

    return service({

        method:"GET",
        url:"/banner?type=2",
    })

    
    
}
