import service from "..";

//获取歌单详情页的数据

//“没有引号代表着这是某个参数，有引号说明这个是事件”

//这个data具体代表着什么？

export function getMusicItemList(data){

    return service({
        method:'GET',
        //这个data是等下要调用的对象
        url:`/playlist/detail?id=${data}`

    })
}


export function getMusicItemLismore(data){

    return service({
        method:'GET',
        //这个data是等下要调用的对象
        url:`/playlist/track/all?id=${data.id}&limit=10&offset=1`

    })
}
